(1, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, product updates, and inspiration', 'We\'re here for you - get tips, product updates and inspiration straight to your email box', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-06-27 22:45:11', NULL, 0, '', NULL, 0),
(2, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connect with your audience', 'Grow your customer base and increase your sales with marketing tools built for WooCommerce.', 'speaker', '{}', 'unactioned', 'woocommerce-admin', '2020-06-27 22:45:11', NULL, 0, '', NULL, 0),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-06-27 22:45:12', NULL, 0, '', NULL, 0),
(4, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Welcome to WooCommerce! Set up your store and start selling', 'We\'re here to help you going through the most important steps to get your store up and running.', 'info', '{}', 'actioned', 'woocommerce-admin', '2020-06-28 08:45:18', NULL, 0, '', NULL, 0),
(5, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order', 'Congratulations on getting your first order from a customer! Learn how to manage your orders.', 'trophy', '{}', 'unactioned', 'woocommerce-admin', '2020-06-28 20:45:20', NULL, 0, '', NULL, 0),
(6, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-06-29 22:45:17', NULL, 0, '', NULL, 0),
(8, 'wc-admin-new-sales-record', 'info', 'en_US', 'New sales record!', 'Woohoo, June 30th was your record day for sales! Net Sales was $3,010,000 beating the previous record of $30 set on June 29th.', 'trophy', '{\"old_record_date\":\"2020-06-29\",\"old_record_amt\":30,\"new_record_date\":\"2020-06-30\",\"new_record_amt\":3010000}', 'unactioned', 'woocommerce-admin', '2020-07-01 22:45:16', NULL, 0, '', NULL, 0),
(9, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://localhost/wordpress/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-06 13:37:01', NULL, 0, '', NULL, 0),
(10, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Give feedback', 'Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-10 13:56:26', NULL, 0, 'plain', '', 0),
(11, 'wc-admin-need-some-inspiration', 'info', 'en_US', 'Do you need some inspiration?', 'Check some of our favorite customer stories from entrepreneurs, agencies, and developers in our global community.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-10 13:56:27', NULL, 0, 'plain', '', 0),
(12, 'wc-admin-learn-more-about-product-settings', 'info', 'en_US', 'Learn more about Product Settings', 'In this video you\'ll find information about configuring product settings, such as how they are displayed, editing inventory options and so on.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-10 13:56:27', NULL, 0, 'plain', '', 0),
(13, 'wc-admin-choose-niche', 'info', 'en_US', 'How to choose a niche for your online store', 'Your niche defines the products and services you develop. It directs your marketing. It focuses your attention on specific problems facing your customers. It differentiates you from the competition. Learn more about the five guiding principles to define your niche.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-10 13:56:27', NULL, 0, 'plain', '', 0);
