(29, 'Remove cart items', '', 'add_action( \'wp_head\', \'wc_clear_cart\' );\r\nfunction wc_clear_cart() {\r\n    if ( wc_get_page_id( \'cart\' ) == get_the_ID() || wc_get_page_id( \'checkout\' ) == get_the_ID() ) {\r\n        return;\r\n    }\r\n    WC()->cart->empty_cart( true );\r\n}', '', 'global', 10, 1, '2020-11-24 21:56:58'),
