INSERT INTO `wp_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`, `modified`) VALUES
(1, 'Example HTML shortcode', 'This is an example snippet for demonstrating how to add an HTML shortcode.\r\n\r\nYou can remove it, or edit it to add your own content.', 'add_shortcode( \'shortcode_name\', function () {\r\n\r\n	$out = \'<p>write your HTML shortcode content here</p>\';\r\n\r\n	return $out;\r\n} );', 'shortcode', 'front-end', 10, 0, '2020-07-01 19:53:35'),
(2, 'Example CSS snippet', 'This is an example snippet for demonstrating how to add custom CSS code to your website.\n\nYou can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n<style>\n\n	/* write your CSS code here */\n\n</style>\n<?php } );\n', 'css', 'front-end', 10, 0, '2020-07-01 16:21:30'),
(3, 'Example JavaScript snippet', 'This is an example snippet for demonstrating how to add custom JavaScript code to your website.\n\nYou can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n<script>\n\n	/* write your JavaScript code here */\n\n</script>\n<?php } );\n', 'javascript', 'front-end', 10, 0, '2020-07-01 16:21:30'),
(4, 'Order snippets by name', 'Order snippets by name by default in the snippets table.', '\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'name\';\n} );\n', 'code-snippets-plugin', 'admin', 10, 0, '2020-07-01 16:21:30'),
(5, 'Order snippets by date', 'Order snippets by last modification date by default in the snippets table.', '\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'modified\';\n} );\n\nadd_filter( \'code_snippets/list_table/default_order\', function () {\n	return \'desc\';\n} );\n', 'code-snippets-plugin', 'admin', 10, 1, '2020-07-01 16:21:30'),
(6, 'Add a custom product data tab Woocommerce', 'Add a custom product data tab woocommerce', 'add_filter(\'wpcf_default_single_campaign_tabs\', \'single_tab_callback\', 9);\r\nfunction single_tab_callback( $tabs = array() ) {\r\n    $tabs[\'extra_tab\'] = array(\r\n        \'title\'     => __( \'Metas\', \'text_domain\' ),\r\n        \'callback\'  => \'tab_content_callback\'\r\n    );\r\n    return $tabs;\r\n}\r\nfunction tab_content_callback(){\r\n	echo do_shortcode( \'[INSERT_ELEMENTOR id=\"191\"]\' );\r\n}', 'woocommerce, wpcrowdfunding', 'global', 10, 0, '2020-07-01 22:42:53'),
(7, 'Cerrar acordeon Elementor', '', 'function elementor_acordeon_cerrado_de_inicio() { ?>\r\n  <script>\r\n    jQuery(document).ready(function() {\r\n      jQuery( \'.elementor-accordion .elementor-tab-title\' ).removeClass( \'elementor-active\' );\r\n      jQuery( \'.elementor-accordion .elementor-tab-content\' ).css( \'display\', \'none\' );\r\n    });\r\n  </script>\r\n<?php }\r\nadd_action( \'wp_footer\', \'elementor_acordeon_cerrado_de_inicio\', 99 );', 'Elementor', 'global', 10, 1, '2020-07-09 21:35:19'),
(8, 'login redirection', '', 'add_action( \'template_redirect\', \'redirect_to_specific_page\' );\r\n\r\nfunction redirect_to_specific_page() {\r\n	if ( is_page(\'registro-campana\')  && !is_user_logged_in()) {\r\n			auth_redirect();			\r\n			//wp_redirect( \'http://localhost/wordpress/logincf\', 302 );\r\n		}\r\n	//var_dump(wp_get_referer());\r\n}', '', 'global', 10, 1, '2020-07-27 14:58:33'),
(11, 'Change Login url', '', 'add_filter( \'login_url\', \'new_login_page\', 10, 3 );\r\nfunction new_login_page( $login_url, $redirect, $force_reauth ) {\r\n    $login_page = home_url( \'/logincf/\' ); //use the slug of your custom login page.\r\n    return add_query_arg( \'redirect_to\', $redirect, $login_page );\r\n}', '', 'global', 10, 1, '2020-07-22 23:15:15'),
(12, 'Mostrar elemento formulario login', 'Originalmente el thema agregaba un campo con el login oculto, y un link para abrilo, se elimina el link y se deja el campo abierto por defecto.', 'function mostrar_login() { ?>\r\n  <script>\r\n    jQuery(document).ready(function() {\r\n      jQuery( \'.wpneo_login_form_div\' ).css( \'display\', \'block\' );\r\n    });\r\n  </script>\r\n<?php }\r\nadd_action( \'wp_footer\', \'mostrar_login\', 99 );', '', 'global', 10, 1, '2020-07-30 21:29:27'),
(19, 'login failed wp-crowfunding', 'Filtro para sobreescribir la funcion \"wpcf_front_end_login_fail\" del archivo \"wp-crowdfuding.php\".\r\n\r\npara manejar la funcion de mensaje en caso de falla de login', 'add_action( \'wp_login_failed\', \'bps_front_end_login_fail\' , 9, 2);  // hook failed login\r\nfunction bps_front_end_login_fail( $username ) {\r\n	\r\n  $referrer = wp_get_referer();\r\n   if ( !empty($referrer) && !strstr($referrer,\'wp-login\') && !strstr($referrer,\'wp-admin\') ) { \r\n        if (strpos($referrer, \'?\') !== false){\r\n            if (strpos($referrer, \'failed\') !== false) {\r\n                wp_redirect( $referrer ); \r\n            }else{\r\n             wp_redirect( $referrer . \'&login=failed\' ); \r\n            }\r\n        }else{\r\n            wp_redirect( $referrer . \'?login=failed\' ); \r\n        }\r\n      exit;\r\n   }\r\n}', '', 'global', 10, 1, '2020-08-05 14:32:03'),
(20, 'modificación login', '', 'add_filter( \'crowdfunding_login_form_handler\', \'bps_crowdfunding_login_form_handler\' , 9, 2);  // hook failed login\r\nfunction bps_crowdfunding_login_form_handler($atts) {\r\n	extract(shortcode_atts(array(\r\n        \'redirect\' => \'\',\r\n        \'form_id\' => \'\',\r\n        \'label_username\' => \'\',\r\n        \'label_password\' => \'\',\r\n        \'label_remember\' => \'\',\r\n        \'label_log_in\' => \'\',\r\n        \'id_username\' => \'\',\r\n        \'id_password\' => \'\',\r\n        \'id_remember\' => \'\',\r\n        \'id_submit\' => \'\',\r\n        \'remember\' => \'\',\r\n        \'value_username\' => \'\',\r\n        \'value_remember\' => \'\',\r\n        \'lost_password\' => \'\',\r\n    ), $atts));\r\n    \r\n    $args = array();\r\n    $args[\'echo\'] = \"0\";\r\n    if(isset($redirect) && $redirect != \"\"){\r\n        $args[\'redirect\'] = esc_url($redirect);\r\n    }\r\n    if(isset($form_id) && $form_id != \"\"){\r\n        $args[\'form_id\'] = $form_id;\r\n    }\r\n    if(isset($label_username) && $label_username != \"\"){\r\n        $args[\'label_username\'] = $label_username;\r\n    }\r\n    if(isset($label_password) && $label_password != \"\"){\r\n        $args[\'label_password\'] = $label_password;\r\n    }\r\n    if(isset($label_remember) && $label_remember != \"\"){\r\n        $args[\'label_remember\'] = $label_remember;\r\n    }\r\n    if(isset($label_log_in) && $label_log_in != \"\"){\r\n        $args[\'label_log_in\'] = $label_log_in;\r\n    }\r\n    if(isset($id_username) && $id_username != \"\"){\r\n        $args[\'id_username\'] = $id_username;\r\n    }\r\n    if(isset($id_password) && $id_password != \"\"){\r\n        $args[\'id_password\'] = $id_password;\r\n    }\r\n    if(isset($id_remember) && $id_remember != \"\"){\r\n        $args[\'id_remember\'] = $id_remember;\r\n    }\r\n    if(isset($id_submit) && $id_submit != \"\"){\r\n        $args[\'id_submit\'] = $id_submit;\r\n    }\r\n    if(isset($remember) && $remember != \"\"){\r\n        $args[\'remember\'] = $remember;\r\n    }\r\n    if(isset($value_username) && $value_username != \"\"){\r\n        $args[\'value_username\'] = $value_username;\r\n    }\r\n    if(isset($value_remember) && $value_remember != \"\"){\r\n        $args[\'value_remember\'] = $value_remember;\r\n    }\r\n    // codigo para tomar la redireccion de la urls\r\n //  $redir = $_GET[\'redirect_to\'];\r\n   // if (isset($redir) && $redir != \"\"){\r\n     //   $args[\'redirect\'] = $redir;\r\n    //}\r\n    $fail = $_GET[\'login\'];\r\n    if (isset($fail)  && $fail != \"\"){\r\n		var_dump(\'hola\');\r\n        ?>\r\n		<html><h3 style=\"color: red\">Login Fallido</h3></html>\r\n        <?php\r\n    }\r\n    \r\n    $login_form = \"\";\r\n    if(is_user_logged_in()){\r\n        $login_form .= wp_loginout(esc_url($_SERVER[\'REQUEST_URI\']), false);\r\n    }\r\n    else{\r\n        $login_form .= wp_login_form($args);\r\n        if(isset($lost_password) && $lost_password != \"0\"){\r\n            $lost_password_link = \'<a href=\"\'.wp_lostpassword_url().\'\">\'.__(\'Lost your password?\', \'crowdfunding-login-form\').\'</a>\';\r\n            $login_form .= $lost_password_link;\r\n        }\r\n        $register_link = \'<a href= \"http://localhost/wordpress/registrate/\" > ¿Aún no tienes cuenta? \'.__(\'Regístrate\', \'crowdfunding-login-form\').\'</a>\';\r\n        $login_form .= $register_link;\r\n    }\r\n    return $login_form;\r\n    \r\n}', '', 'global', 10, 1, '2020-08-05 15:21:25'),
(21, 'terminos y condiciones woocomerce', '', 'add_action(\'woocommerce_checkout_update_order_meta\', \'woo_save_terms_and_conditions_status\');\r\nfunction woo_save_terms_and_conditions_status( $order_id ) {\r\n    if ($_POST[\'terms\']) update_post_meta( $order_id, \'_terms\', esc_attr($_POST[\'terms\']));\r\n}\r\nadd_action( \'woocommerce_admin_order_data_after_billing_address\', \'woo_display_terms_and_conditions_status\', 10, 1 );\r\nfunction woo_display_terms_and_conditions_status($order){\r\n    $terms = get_post_meta( $order->id, \'_terms\', true );\r\n    $terms_status = ( $terms == \'on\' ? __(\'aceptado\') : __(\'undefined\') );\r\n    echo \'<p><strong>\'.__(\'Términos y condiciones\').\':</strong> \' . $terms_status . \'</p>\';\r\n}', '', 'global', 10, 1, '2020-08-14 21:04:41'),
(24, 'Remove \"Backed User\" tab woocommerce', '', '/**\r\n * Remove product data tabs\r\n */\r\nadd_filter( \'woocommerce_product_tabs\', \'woo_remove_product_tabs\', 98 );\r\n\r\nfunction woo_remove_product_tabs( $tabs ) {\r\n\r\n    unset( $tabs[\'backed_user\'] );      	// Remove the backed_user tab\r\n   \r\n\r\n    return $tabs;\r\n}', '', 'global', 10, 1, '2020-09-08 16:25:38'),
(25, 'dont save billing data', '', ' if (! empty($_POST[\'dont_save_data\'])){\r\n            if ($_POST[\'dont_save_data\'] === \'true\'){\r\n                add_filter( \'woocommerce_checkout_update_customer_data\', \'__return_false\' );   \r\n            }\r\n        }\r\n\r\n//add_filter( \'woocommerce_checkout_update_customer_data\', \'__return_false\' );', '', 'global', 10, 1, '2020-09-11 16:59:26');
